import React, { useState } from 'react';
import client from '../api/maintenanceApi';
import './AddMaintenanceForm.css'; // Import the CSS file for styles

const AddMaintenanceForm: React.FC = () => {
    const [formData, setFormData] = useState<{
        buildingName: string;
        address: string;
        adminName: string;
        phoneNumber: string;
        periodicDate: string;
        description: string;
        monthlyFee: number;
        yearlyFee: number;
        paidFee: number;
      }>({
        buildingName: '',
        address: '',
        adminName: '',
        phoneNumber: '',
        periodicDate: '',
        description: '',
        monthlyFee: 0,
        yearlyFee: 0,
        paidFee: 0,
      });


  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;

    // Convert monthlyFee to a number if the field is updated
    if (name === 'monthlyFee') {
      setFormData({ ...formData, [name]: parseFloat(value) });
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await client.post('/maintenances', formData);
      alert('Maintenance added successfully');
    } catch (error) {
      console.error('Error adding maintenance:', error);
      alert('An error occurred while adding maintenance.');
    }
  };

  return (
    <div className="form-container">
      <h2>Add New Maintenance</h2>
      <form onSubmit={handleSubmit} className="maintenance-form">
        <input
          type="text"
          name="buildingName"
          value={formData.buildingName}
          onChange={handleChange}
          placeholder="Building Name"
          required
        />
        <input
          type="text"
          name="address"
          value={formData.address}
          onChange={handleChange}
          placeholder="Address"
          required
        />
        <input
          type="text"
          name="adminName"
          value={formData.adminName}
          onChange={handleChange}
          placeholder="Admin Name"
          required
        />
        <input
          type="text"
          name="phoneNumber"
          value={formData.phoneNumber}
          onChange={handleChange}
          placeholder="Phone Number"
        />
        <input
          type="date"
          name="periodicDate"
          value={formData.periodicDate}
          onChange={handleChange}
        />
        <input
          type="number"
          name="monthlyFee"
          value={formData.monthlyFee}
          onChange={handleChange}
          placeholder="Monthly Fee"
          required
        />
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          placeholder="Description"
        ></textarea>
        <button type="submit" className="submit-button">Add Maintenance</button>
      </form>
    </div>
  );
};

export default AddMaintenanceForm;
