import React, { useState } from 'react';
import { useMutation } from '@tanstack/react-query';
import api from '../services/api';
import { useDispatch } from 'react-redux';
import { addMaintenance } from '../redux/slices/maintenanceSlice';
import { Maintenance } from '../redux/slices/maintenanceSlice';

const AddNew: React.FC = () => {
  const dispatch = useDispatch();
  const [formData, setFormData] = useState({
    buildingName: '',
    address: '',
    adminName: '',
    monthlyFee: 0,
    description: '',
  });

  const mutation = useMutation({
    mutationFn: async () => {
      const response = await api.post('/maintenance', formData);
      return response.data;
    },
    onSuccess: (data: unknown) => {
      // Tür kontrolü ekleyerek tür dönüşümünü güvence altına alıyoruz
      if (data && typeof data === 'object' && 'id' in data) {
        const maintenanceData = data as Maintenance;
        dispatch(addMaintenance(maintenanceData));
        alert('New maintenance added successfully');
        window.location.href = '/';
      } else {
        console.error('Unexpected data format:', data);
      }
    },
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: name === 'monthlyFee' ? parseFloat(value) : value,
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    mutation.mutate();
  };

  return (
    <div>
      <h1>Add New Maintenance</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="buildingName"
          placeholder="Building Name"
          value={formData.buildingName}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="address"
          placeholder="Address"
          value={formData.address}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="adminName"
          placeholder="Admin Name"
          value={formData.adminName}
          onChange={handleChange}
          required
        />
        <input
          type="number"
          name="monthlyFee"
          placeholder="Monthly Fee"
          value={formData.monthlyFee}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="description"
          placeholder="Description"
          value={formData.description}
          onChange={handleChange}
          required
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
};

export default AddNew;
