import React, { useState } from 'react';
import client from '../api/maintenanceApi';
import './AddMaintenanceForm.css'; // Import the CSS file for styles

const AddMaintenanceForm: React.FC = () => {
  const [formData, setFormData] = useState<{
    buildingName: string;
    address: string;
    adminName: string;
    phoneNumber: string;
    periodicDate: string;
    description: string;
  }>({
    buildingName: '',
    address: '',
    adminName: '',
    phoneNumber: '',
    periodicDate: '',
    description: ''
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await client.post('/maintenances', formData);
      alert('Maintenance added successfully');
    } catch (error) {
      console.error('Error adding maintenance:', error);
      alert('An error occurred while adding maintenance.');
    }
  };

  return (
    <div className="form-container">
      <h2>Add New Maintenance</h2>
      <form onSubmit={handleSubmit} className="maintenance-form">
        <input type="text" name="buildingName" value={formData.buildingName} onChange={handleChange} placeholder="Building Name" required />
        <input type="text" name="address" value={formData.address} onChange={handleChange} placeholder="Address" required />
        <input type="text" name="adminName" value={formData.adminName} onChange={handleChange} placeholder="Admin Name" required />
        <input type="text" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} placeholder="Phone Number" />
        <input type="date" name="periodicDate" value={formData.periodicDate} onChange={handleChange} />
        <textarea name="description" value={formData.description} onChange={handleChange} placeholder="Description"></textarea>
        <button type="submit" className="submit-button">Add Maintenance</button>
      </form>
    </div>
  );
};

export default AddMaintenanceForm;
