import React, { useEffect, useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import client from './api/maintenanceApi';
import MaintenanceItem from './components/MaintenanceItem';
import { Maintenance } from './types';
import AddMaintenanceForm from './components/AddMaintenanceForm'; // Ekleyeceğiniz form sayfası

const App: React.FC = () => {
  const [maintenances, setMaintenances] = useState<Maintenance[]>([]);

  useEffect(() => {
    const fetchMaintenances = async () => {
      try {
        const response = await client.get<Maintenance[]>('/maintenances');
        setMaintenances(response.data);
      } catch (error) {
        console.error('Error fetching maintenances:', error);
        alert('An error occurred while fetching maintenance data.');
      }
    };

    fetchMaintenances();
  }, []);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={
          <div>
            <h1>Maintenances</h1>
            {maintenances.map((maintenance) => (
              <MaintenanceItem key={maintenance.id} maintenance={maintenance} />
            ))}
          </div>
        } />
        <Route path="/add-maintenance" element={<AddMaintenanceForm />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;
