import React, { useState } from 'react';
import { client } from '../api/client';

type Maintenance = {
  monthlyFee: number;
  description: string;
};

const AddMaintenanceForm: React.FC = () => {
  const [formData, setFormData] = useState<Maintenance>({
    monthlyFee: 0,
    description: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: name === 'monthlyFee' ? Number(value) : value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await client.post('/maintenances', formData);
      alert('Maintenance added successfully!');
      setFormData({ monthlyFee: 0, description: '' }); // Formu temizle
    } catch (error) {
      console.error('Error adding maintenance:', error);
      alert('An error occurred while adding maintenance.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      <label>
        Monthly Fee:
        <input
          type="number"
          name="monthlyFee"
          value={formData.monthlyFee}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Description:
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          required
        />
      </label>
      <button type="submit">Add Maintenance</button>
    </form>
  );
};

export default AddMaintenanceForm;
