import React, { useEffect, useState } from 'react';
import client from './api/maintenanceApi';
import MaintenanceItem from './components/MaintenanceItem';
import { Maintenance } from './types'; // T端r端 import edin

const App: React.FC = () => {
  const [maintenances, setMaintenances] = useState<Maintenance[]>([]);

  useEffect(() => {
    const fetchMaintenances = async () => {
      try {
        const response = await client.get('/maintenances');
        setMaintenances(response.data);
      } catch (error) {
        console.error('Error fetching maintenances:', error);
        alert('An error occurred while fetching maintenance data.');
      }
    };

    fetchMaintenances();
  }, []);

  return (
    <div>
      <h1>Maintenances</h1>
      {maintenances.map((maintenance) => (
        <MaintenanceItem key={maintenance.id} maintenance={maintenance} />
      ))}
    </div>
  );
};

export default App;
