import React, { useEffect, useState } from 'react';
import { getMaintenances, Maintenance } from './api/maintenanceApi';
import withBackgroundColor from './hoc/withBackgroundColor';
import MaintenanceItem from './components/MaintenanceItem';
import AddMaintenanceForm from './components/AddMaintenanceForm';

const App: React.FC = () => {
  const [maintenances, setMaintenances] = useState<Maintenance[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      const data = await getMaintenances();
      setMaintenances(data);
    };
    fetchData();
  }, []);

  const EnhancedMaintenanceItem = withBackgroundColor(MaintenanceItem);

  return (
    <div>
      <h1>Maintenances</h1>
      <AddMaintenanceForm />
      {maintenances.map((maintenance) => (
        <EnhancedMaintenanceItem key={maintenance.id} maintenance={maintenance} />
      ))}
    </div>
  );
};

export default App;
