import React, { useState } from 'react';
import { Maintenance } from '../pages/Home';

interface ListItemProps extends Maintenance {
  isLocked: boolean;
  onLockToggle: () => void;
}

const ListItem: React.FC<ListItemProps> = ({ buildingName, address, adminName, monthlyFee, description, isLocked, onLockToggle }) => {
  // State yönetimi
  const [formData, setFormData] = useState({
    buildingName,
    address,
    adminName,
    monthlyFee,
    description
  });

  // Form inputlarını güncelleme
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  return (
    <div style={{ border: '1px solid #ddd', padding: '20px', margin: '10px 0', borderRadius: '8px' }}>
      <h3>{buildingName}</h3>
      <p>{address}</p>
      <p>{adminName}</p>
      <p>Monthly Fee: {monthlyFee}</p>
      <p>{description}</p>

      {/* Kilit butonu */}
      <button onClick={onLockToggle}>
        {isLocked ? 'Unlock' : 'Lock'}
      </button>

      {/* Form elemanları */}
      <form>
        <div>
          <label htmlFor="buildingName">Building Name</label>
          <input
            type="text"
            name="buildingName"
            id="buildingName"
            value={formData.buildingName}
            onChange={handleChange}
            readOnly={isLocked} // Kilitli olduğunda readonly olacak
            style={{ backgroundColor: isLocked ? '#f0f0f0' : 'white' }}
          />
        </div>
        <div>
          <label htmlFor="address">Address</label>
          <input
            type="text"
            name="address"
            id="address"
            value={formData.address}
            onChange={handleChange}
            readOnly={isLocked}
            style={{ backgroundColor: isLocked ? '#f0f0f0' : 'white' }}
          />
        </div>
        <div>
          <label htmlFor="adminName">Admin Name</label>
          <input
            type="text"
            name="adminName"
            id="adminName"
            value={formData.adminName}
            onChange={handleChange}
            readOnly={isLocked}
            style={{ backgroundColor: isLocked ? '#f0f0f0' : 'white' }}
          />
        </div>
        <div>
          <label htmlFor="monthlyFee">Monthly Fee</label>
          <input
            type="number"
            name="monthlyFee"
            id="monthlyFee"
            value={formData.monthlyFee}
            onChange={handleChange}
            readOnly={isLocked}
            style={{ backgroundColor: isLocked ? '#f0f0f0' : 'white' }}
          />
        </div>
        <div>
          <label htmlFor="description">Description</label>
          <input
            type="text"
            name="description"
            id="description"
            value={formData.description}
            onChange={handleChange}
            readOnly={isLocked}
            style={{ backgroundColor: isLocked ? '#f0f0f0' : 'white' }}
          />
        </div>
      </form>
    </div>
  );
};

export default ListItem;
