// src/components/AddMaintenanceForm.tsx
import React, { useState } from 'react';
import axios from '../axiosConfig';

const AddMaintenanceForm = () => {
  const [formData, setFormData] = useState({
    buildingName: '',
    address: '',
    adminName: '',
    description: '',
    monthlyFee: 0,
  });

  // handleChange fonksiyonunda event tipi belirtildi
  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // handleSubmit fonksiyonunda event tipi belirtildi
  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      const response = await axios.post('/maintenance', formData);
      console.log('Yeni kayıt başarıyla eklendi:', response.data);
      alert('Kayıt başarıyla eklendi');
      // Formu sıfırlama
      setFormData({
        buildingName: '',
        address: '',
        adminName: '',
        description: '',
        monthlyFee: 0,
      });
    } catch (error) {
      console.error('Kayıt eklenirken hata oluştu:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Building Name:</label>
        <input type="text" name="buildingName" onChange={handleChange} value={formData.buildingName} />
      </div>
      <div>
        <label>Address:</label>
        <input type="text" name="address" onChange={handleChange} value={formData.address} />
      </div>
      <div>
        <label>Admin Name:</label>
        <input type="text" name="adminName" onChange={handleChange} value={formData.adminName} />
      </div>
      <div>
        <label>Description:</label>
        <textarea name="description" onChange={handleChange} value={formData.description}></textarea>
      </div>
      <div>
        <label>Monthly Fee:</label>
        <input type="number" name="monthlyFee" onChange={handleChange} value={formData.monthlyFee} />
      </div>
      <button type="submit">Kayıt Ekle</button>
    </form>
  );
};

export default AddMaintenanceForm;
