// src/hoc/withBackgroundColor.js
import React from 'react';

const withBackgroundColor = (WrappedComponent) => {
  return (props) => {
    const { data } = props;
    let backgroundColor = 'white'; // Varsayılan renk

    if (data.monthlyFee < 1000) {
      backgroundColor = 'green';
    } else if (data.monthlyFee > 1000 && data.monthlyFee <= 5000) {
      backgroundColor = 'blue';
    } else if (data.monthlyFee > 5000) {
      backgroundColor = 'red';
    }

    return (
      <div style={{ backgroundColor }}>
        <WrappedComponent {...props} />
      </div>
    );
  };
};

export default withBackgroundColor;
