// src/pages/AddNew.tsx
import React, { useState } from 'react';
import api from '../services/api';

const AddNew: React.FC = () => {
  const [formData, setFormData] = useState({
    buildingName: '',
    address: '',
    adminName: '',
    monthlyFee: 0,
    description: '',
  });

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    try {
      await api.post('/maintenance', formData);
      alert('New maintenance added successfully');
      window.location.href = '/';
    } catch (error) {
      console.error('Error adding new maintenance:', error);
      alert('Failed to add new maintenance');
    }
  };

  return (
    <div>
      <h1>Add New Maintenance</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="buildingName" placeholder="Building Name" onChange={handleChange} required />
        <input type="text" name="address" placeholder="Address" onChange={handleChange} required />
        <input type="text" name="adminName" placeholder="Admin Name" onChange={handleChange} required />
        <input type="number" name="monthlyFee" placeholder="Monthly Fee" onChange={handleChange} required />
        <input type="text" name="description" placeholder="Description" onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default AddNew;
