// src/components/MaintenanceList.tsx
import React, { useEffect, useState } from 'react';
import axios from '../axiosConfig';
import withBackgroundColor from '../hoc/withBackgroundColor';

// Maintenance arayüzünü tanımlayın
interface Maintenance {
  id: number;
  buildingName: string;
  address: string;
  adminName: string;
  description: string;
  monthlyFee: number;
  paidFee: number;
  remainderFee: number;
}

const MaintenanceList: React.FC = () => {
  const [maintenances, setMaintenances] = useState<Maintenance[]>([]);

  useEffect(() => {
    const fetchMaintenances = async () => {
      try {
        const response = await axios.get<Maintenance[]>('/maintenances');
        setMaintenances(response.data);
      } catch (error) {
        console.error('Veri getirilirken hata oluştu:', error);
      }
    };

    fetchMaintenances();
  }, []);

  return (
    <div>
      <h2>Bakım Kayıtları</h2>
      <ul>
        {maintenances.map((item) => (
          <ListItem key={item.id} data={item} />
        ))}
      </ul>
    </div>
  );
};

// ListItem bileşenini tanımlayın ve prop tipini belirtin
interface ListItemProps {
  data: Maintenance;
}

const ListItem: React.FC<ListItemProps> = ({ data }) => (
  <li>
    <strong>{data.buildingName}</strong> - {data.description}
    <p>Adres: {data.address}</p>
    <p>Yönetici Adı: {data.adminName}</p>
    <p>Aylık Ücret: {data.monthlyFee}</p>
    <p>Ödenen Ücret: {data.paidFee}</p>
    <p>Kalan Ücret: {data.remainderFee}</p>
  </li>
);

// withBackgroundColor HOC'sine tür belirterek ListItem'ı sarıyoruz
export default withBackgroundColor(ListItem);
