import React from 'react';

interface Maintenance {
  id: number;
  buildingName: string;
  address: string;
  adminName: string;
  phoneNumber: string;
  periodicDate: string;
  identification: string;
  color: string;
  monthlyFee: number;
  yearlyFee: number;
  paidFee: number;
  revision: number;
  remainderFee: number;
  expense: number;
  profit: number;
  description: string;
}

interface MaintenanceItemProps {
  maintenance: Maintenance;
}

const MaintenanceItem: React.FC<MaintenanceItemProps> = ({ maintenance }) => {
  let backgroundColor = 'white'; // Default color

  if (maintenance.monthlyFee < 1000) {
    backgroundColor = 'green';
  } else if (maintenance.monthlyFee >= 1000 && maintenance.monthlyFee <= 5000) {
    backgroundColor = 'blue';
  } else if (maintenance.monthlyFee > 5000) {
    backgroundColor = 'red';
  }

  return (
    <div style={{ backgroundColor, padding: '10px', margin: '5px' }}>
      <h2>{maintenance.buildingName}</h2>
      <p>{maintenance.address}</p>
      <p>Admin: {maintenance.adminName}</p>
      <p>Monthly Fee: ${maintenance.monthlyFee}</p>
    </div>
  );
};

export default MaintenanceItem;
