// src/components/AddMaintenanceForm.js
import React, { useState } from 'react';
import axios from '../axiosConfig';

const AddMaintenanceForm = () => {
  const [formData, setFormData] = useState({
    buildingName: '',
    address: '',
    adminName: '',
    description: '',
    monthlyFee: 0,
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/maintenance', formData);
      console.log('Yeni kayıt başarıyla eklendi:', response.data);
    } catch (error) {
      console.error('Kayıt eklenirken hata oluştu:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label>Building Name:</label>
        <input type="text" name="buildingName" onChange={handleChange} />
      </div>
      <div>
        <label>Address:</label>
        <input type="text" name="address" onChange={handleChange} />
      </div>
      <div>
        <label>Admin Name:</label>
        <input type="text" name="adminName" onChange={handleChange} />
      </div>
      <div>
        <label>Description:</label>
        <textarea name="description" onChange={handleChange}></textarea>
      </div>
      <div>
        <label>Monthly Fee:</label>
        <input type="number" name="monthlyFee" onChange={handleChange} />
      </div>
      <button type="submit">Kayıt Ekle</button>
    </form>
  );
};

export default AddMaintenanceForm;
