import React from 'react';

type Maintenance = {
  id: string;
  monthlyFee: number;
  description: string;
};

const withBackgroundColor = (
  Component: React.ComponentType<{ maintenance: Maintenance }>
) => {
  return ({ maintenance }: { maintenance: Maintenance }) => {
    let bgColor = 'white';

    if (maintenance.monthlyFee > 5000) bgColor = 'red.500';
    else if (maintenance.monthlyFee > 1000) bgColor = 'blue.500';
    else bgColor = 'green.500';

    return (
      <div style={{ backgroundColor: bgColor, padding: '10px', margin: '10px' }}>
        <Component maintenance={maintenance} />
      </div>
    );
  };
};

export default withBackgroundColor;
