// src/components/MaintenanceList.js
import React, { useEffect, useState } from 'react';
import axios from '../axiosConfig';
import withBackgroundColor from '../hoc/withBackgroundColor';

const MaintenanceList = () => {
  const [maintenances, setMaintenances] = useState([]);

  useEffect(() => {
    const fetchMaintenances = async () => {
      try {
        const response = await axios.get('/maintenances');
        setMaintenances(response.data);
      } catch (error) {
        console.error('Veri getirilirken hata oluştu:', error);
      }
    };

    fetchMaintenances();
  }, []);

  return (
    <div>
      <h2>Bakım Kayıtları</h2>
      <ul>
        {maintenances.map((item) => (
          <ListItem key={item.id} data={item} />
        ))}
      </ul>
    </div>
  );
};

// ListItem bileşenini HOC ile sararak arka plan rengini değiştireceğiz
const ListItem = withBackgroundColor(({ data }) => (
  <li>
    <strong>{data.buildingName}</strong> - {data.description}
  </li>
));

export default MaintenanceList;
